import { useEffect, useState } from "react";

const MainPoster = () => {
const items = [
  {
    name: "Event #1",
    description: "Lorem ipsum dolor sit amet consectetur, adipisicing elit...",
    image: "data:image/jpeg;base64,"
  },
  {
    name: "Event #2",
    description: "Repellat molestiae consequatur libero nam optio similique...",
    image: "data:image/jpeg;base64,"
  },
  {
    name: "Event #3",
    description: "Quasi mollitia qui quae sunt voluptate dicta...",
    image: "data:image/jpeg;base64,"
  },
  {
    name: "Event #4",
    description: "Libero vero quo tempora cupiditate animi nisi...",
    image: "data:image/jpeg;base64,"
  }
];

    const [currentIndex, setCurrentIndex] = useState(0);
    const [posterData, setPosterData] = useState(items[0]);
    const [isVisible, setIsVisible] = useState(true);

    useEffect(() => {
        const intervalId = setInterval(() => {
            setIsVisible(false); // fade out
            setTimeout(() => {
                const nextIndex = (currentIndex + 1) % items.length;
                setCurrentIndex(nextIndex);
                setPosterData(items[nextIndex]);
                setIsVisible(true);
            }, 700); 
        }, 5000);

        return () => clearInterval(intervalId);
    }, [currentIndex]);
    return (
        <>
            <div
                className={`transition-opacity duration-[1000ms] ease-in-out ${isVisible ? "opacity-100" : "opacity-0"
                    } bg-gradient-to-r from-slate-900  to-blue-900 w-full h-auto rounded-2xl flex flex-col-reverse md:flex-row items-center justify-around gap-4 p-4 overflow-hidden`}
            >
                {/* Content */}
                <div className="flex flex-col justify-center text-white text-center w-full md:w-[40%]">
                    <p className="font-bold text-xl mb-2">{posterData.name}</p>
                    <p>{posterData.description}</p>
                </div>

                {/* Image */}
                <div
  className="w-full md:w-[30%] h-[40vh] rounded-2xl bg-center bg-cover"
  style={{ backgroundImage: `url(${posterData.image})` }}
></div>

            </div>
            <div className=" flex items-center justify-end  ">
                <p className=" text-white mt-2  hover:cursor-pointer">See all...</p>
            </div>
        </>
    )
}

export default MainPoster;